import { JSX } from "solid-js";
import { TransitionCallbacks } from "solid-react-transition";
export interface FadeProps extends TransitionCallbacks {
    className?: string;
    in?: boolean;
    mountOnEnter?: boolean;
    unmountOnExit?: boolean;
    appear?: boolean;
    timeout?: number | {
        appear?: number;
        enter?: number;
        exit?: number;
    };
    children?: JSX.Element;
    transitionClasses?: Record<string, string>;
}
declare const Fade: (p: FadeProps) => JSX.Element;
export default Fade;
