import { mergeProps, splitProps } from "solid-js";
import classNames from "./classnames";
import { useBootstrapPrefix } from "./ThemeProvider";
import AbstractModalHeader from "./AbstractModalHeader";
const defaultProps = {
    closeLabel: "Close",
    closeButton: false,
};
const ModalHeader = (p) => {
    const [local, props] = splitProps(mergeProps(defaultProps, p), ["bsPrefix", "className"]);
    const bsPrefix = useBootstrapPrefix(local.bsPrefix, "modal-header");
    return (<AbstractModalHeader {...props} className={classNames(local.className, bsPrefix)}>
      {props.children}
    </AbstractModalHeader>);
};
export default ModalHeader;
